package operators;

public class Basic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(10+30);    //40
		int n=10+30;  
		System.out.println(n);   //40
		int a=10;
		int b=20;
		System.out.println(a+b); //30
		//Assignment operator =,+=,-=,*=,/=,%=.
		int x=30;
		x+=1;  //x=x+1;
		System.out.println(x);
	}

}
